$r=curl https://api.github.com/repos/devklick/GDMan/releases/latest | ConvertFrom-Json;$p=($r.assets | where {$_.name -like '*win-x64.zip'}).browser_download_url; Invoke-WebRequest $p -OutFile "$HOME\Downloads\gdman.zip"; Expand-Archive -LiteralPath "$HOME\Downloads\gdman.zip" -DestinationPath "$Env:AppData\gdman" -Force; Remove-Item -Path "$HOME\Downloads\gdman.zip"; $path=[environment]::GetEnvironmentVariable("PATH", "USER"); if ($PATH -notlike "*C:\Users\user\AppData\Roaming\gdman") { [environment]::SetEnvironmentVariable("PATH", "$path;$Env:AppData\gdman", "USER")}